    }
    openDialog=(event)=>{
        let image=event.target.files[0];

        if(image==undefined || image==null)
            return;

        const thisContext=this;
        var uploadTask = firebase.storage().ref("status").child(image.name).put(image);
        uploadTask.on(
          "state_changed",
          function (snapshot) {

