import React, { Component } from 'react';
import "./Mainpage.css";
import { Grid } from '@material-ui/core';
import LeftSide from './LeftSidePanel/LeftSide';
import StatusBar from './StatusBar/StatusBar';
import UploadSection from './UploadSection/UploadSection';
import PostContainer from './PostContainer/PostContainer';
import RightSide from './RighrSidePanel/RightSide';

class Layout extends Component {
    constructor(props) {
        super(props);
        this.primaryFun = this.letsUpdate.bind(this);
        this.state = {  
            shouldUpdate: true
        }
    }
    letsUpdate=()=>{
      //  this.state.shouldUpdate ? this.setState({shouldUpdate: false}):this.setState({shouldUpdate: true});
        this.child.getData();
    }
    render() { 
        return ( 
            <div className="mainpage__container">
                <Grid container >
                    <Grid item xs={3}>
                        <LeftSide />
                    </Grid>
                    <Grid item xs={6} className="middleContainer">

                        <StatusBar />
                        <UploadSection update={this.letsUpdate} />
                        <PostContainer variable={this.shouldUpdate} update={this.letsUpdate} />

                    </Grid>
                    <Grid item xs={3}>
                        <RightSide />
                    </Grid>
                </Grid>
            </div>
         );
    }
}

export default Layout;
