import React, { Component } from 'react';
import "./PostContainer.css";
import { Paper, Avatar } from '@material-ui/core';
import post from "../../../../images/post.jpeg";
import like from "../../../../images/like.png";
import likebutton from "../../../../images/likebutton.png";
import commentbutton from "../../../../images/comment.png";
import sharebutton from "../../../../images/share.png";

class Post extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }
    isImageAvailable=(data)=>{
        return data==""?false:true;
    }
    render() { 
        return ( 
            <div>
                <Paper className="post__container">
                    {/* header */}
                    <div className="post__header">
                        <div className="post__header_img">
                            <Avatar src="https://scontent.fccu3-1.fna.fbcdn.net/v/t1.6435-1/p148x148/162713656_4172696726074274_4057600099221701280_n.jpg?_nc_cat=109&ccb=1-3&_nc_sid=dbb9e7&_nc_ohc=JZF-KE3J5i8AX_bfiWf&_nc_ht=scontent.fccu3-1.fna&tp=6&oh=d777e7f1cac41c18c54d15ddd673033b&oe=608F5148"
 className="post_img" />
                        </div>
                        <div className="post__header_text"> 
                            {this.props.object.user_name}
                        </div>
                    </div> 

                    {/* description */}
                    <div className="post__description">
                        {this.props.object.description}
                    </div>
                    {/* image */}
                    <div className="post__image">
                        {
                            this.isImageAvailable(this.props.object.postImgURL) ? <img src={this.props.object.postImgURL} width="600px" /> : <span></span>
                        }
                    </div>
                    {/* like count */}
                    <div className="post__likeCountContainer">
                        <div className="post__like">
                            <img className="post__img" src={like} />
                        </div>
                        <div className="post__likecount">
                        {this.props.object.like}
                        </div>
                    </div>
                    {/* like share box */}
                    <div className="post__likeShare">
                        <div className="post__tab">
                            <div className="post__tabfirst">
                                <img className="post__tabimg" src={likebutton} />
                            </div>
                            <div className="post__tabtext">
                                Like
                            </div>
                        </div>

                        <div className="post__tab">
                            <div className="post__tabfirst">
                                <img className="post__tabimg" src={commentbutton} />
                            </div>
                            <div className="post__tabtext">
                                Comment
                            </div>
                        </div>

                        <div className="post__tab">
                            <div className="post__tabfirst">
                                <img className="post__tabimg" src={sharebutton} />
                            </div>
                            <div className="post__tabtext">
                                Share
                            </div>
                        </div>
                    </div>
                    {/* comment box */}
                    <div className="upload__comment">
                        <div className="upload__top">
                            <div>
                                <Avatar src="https://scontent.fccu3-1.fna.fbcdn.net/v/t1.6435-1/p148x148/162713656_4172696726074274_4057600099221701280_n.jpg?_nc_cat=109&ccb=1-3&_nc_sid=dbb9e7&_nc_ohc=JZF-KE3J5i8AX_bfiWf&_nc_ht=scontent.fccu3-1.fna&tp=6&oh=d777e7f1cac41c18c54d15ddd673033b&oe=608F5148" className="upload_img"/>
                            </div>
                            <div>
                                <input className="upload__box" placeholder="What's on your mind ?" type="text" />
                            </div>
                        </div>
                    </div>

                </Paper>
            </div>
         );
    }
}

export default Post;
